mapper.update:
UPDATE MEMBER m
    INNER JOIN SOCIAL_MEMBER sm ON m.member_id = sm.member_id
SET m.email = #{email},
    m.picture = #{picture}
WHERE m.email = #{email}
AND sm.provider_type = #{registrationId}
AND m.use_yn = 'Y'
AND m.cert_yn = 'Y';

mapper.select:
SELECT COUNT(*) AS cnt
FROM MEMBER m
WHERE m.account = #{account}
