mapper.update:
UPDATE MEMBER M
    INNER JOIN SOCIAL_MEMBER SM ON M.MEMBER_ID = SM.MEMBER_ID
SET M.EMAIL = #{email},
    M.PICTURE = #{picture}
WHERE M.EMAIL = #{email}
AND SM.PROVIDER_TYPE = #{registrationId}
AND M.USE_YN = 'Y'
AND M.CERT_YN = 'Y';

mapper.select:
SELECT COUNT(*) AS CNT
FROM MEMBER M
WHERE M.ACCOUNT = #{account}
