mapper.insert:
INSERT INTO POST_FILE (
    post_file_id
    , post_id
    , origin_file_name
    , stored_file_name
    , file_path
    , file_size
    , file_exp
    , download_cnt
    , del_yn
    , create_date
    , delete_date
    , file_attached
) VALUES

    (
        #{file.postFileId}
        , #{file.postId}
        , #{file.originFileName}
        , #{file.storedFileName}
        , #{file.filePath}
        , #{file.fileSize}
        , #{file.fileExp}
        , 0
        , 'N'
        , NOW()
        , NULL
        , #{file.fileAttached}
    )

mapper.update:
UPDATE POST_FILE
SET download_cnt = download_cnt + 1
WHERE post_file_id = #{postFileId}

mapper.select:
SELECT COUNT(*) AS cnt
FROM POST_FILE
WHERE post_id = #{postFileId}
